package com.onlineshop.db;

import com.onlineshop.models.Post;
import com.onlineshop.models.User;

import java.util.ArrayList;
import java.util.List;

public class Db {

    public static final List<Post> posts = new ArrayList<>();

    public static final List<User> users = new ArrayList<>();

    static {
        posts.add(new Post("headphones", "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8M3x8cHJvZHVjdHxlbnwwfHwwfHw%3D&w=1000&q=80"));
        posts.add(new Post("camera", "data:image/jpeg;base64,"));
    }

    public static User getUserByUsername(String username) {
        for (User u: users) {
            if (u.getUserName().equals(username)) return u;
        }
        return null;
    }

    public static boolean userNameExists(User user) {
        String userName = user.getUserName();
        for (User u : Db.users) {
            if (userName.equals(u.getUserName())) return true;
        }
        return false;
    }
}
